const ajax = new XMLHttpRequest();
const URL = 'https://api.hnpwa.com/v0/news/1.json'
const CONTENT_URL = 'https://api.hnpwa.com/v0/item/@id.json'
const content = document.createElement("div")
const container = document.getElementById("root")

const ul = document.createElement('ul');
container.appendChild(ul)
container.appendChild(content)

const store = {
    currentPage: 1,
    feeds: [],
}

function newsFeed() {
    let newsFeed = store.feeds
    const newsList = []
    let template = `
        <div class="container mx-auto p-4">
            <h1>Hacker News</h1>
            <ul>
                {{__news_feed__}}
            </ul>
            <div>
                <a href='#/page/{{__prev_page__}}'></a>
                <a href='#/page/{{__next_page__}}'></a>
            </div>
        </div>
    `
    if (newsFeed.length == 0) {
        newsFeed = store.feeds = makeFeeds(getData(URL))
    }

    for (let i = (store.currentPage - 1) * 10; i < store.currentPage * 10; i++) {
        newsList.push(
            `
            <li class="${newsFeed[i].read ? 'bg-green-100' : 'bg-white'}">
            <a href="#/show/${newsFeed[i].id}">
            ${newsFeed[i].title}  ${newsFeed[i].comments_count}
            </a
            </li>
        `
        )
    }
    template = template.replace('{{__news_feed__}}', newsList.join(''))
    template = template.replace('{{__prev_page__}}', store.currentPage > 1 ? store.currentPage - 1 : 1)
    template = template.replace('{{__next_page__}}', store.currentPage * 10 >= newsFeed.length ? store.currentPage : store.currentPage + 1)
    container.innerHTML = template
}

function makeFeeds(feeds) {
    for(let i=0;i<feeds.length;i++) {
        feeds[i].read = false;
    }
    return feeds;
}

function newsContent() {
    const id = location.hash.substring(7)
    const newsContent = getData(CONTENT_URL.replace('@id', id))
    let template =
        `
    <h1 class="text-red-400">${newsContent.title}</h1>
    <div>
      ${newsContent.content}
    </div>
    {{__comments__}}

    <div>
      <a href="#/page/${store.currentPage}"></a>
    </div>

  `;
    for(let i=0;i<store.feeds.length;i++) {
        if(store.feeds[i].id === Number(id)) {
            store.feeds[i].read = true;
            break
        }
    }  
    function makeComment(comments, depth) {
        const commentString = [];
        for (let i = 0; i < comments.length; i++) {
            commentString.push(`
            <div style="padding-left:${depth * 40}px;">
            <div class="text-gray-400">
              <strong>${comments[i].user}</strong>${comments[i].time_ago}
            </div>
            ${comments[i].content}
            </div>
            `)
            console.log(comments[i].content)
            if(comments[i].comments.length > 0) {
                commentString.push(makeComment(comments[i].comments, depth+1))
            }
        }
        return commentString.join('')
    }

    template = template.replace('{{__comments__}}', makeComment(newsContent.comments, 1))

    container.innerHTML = template

}

function router() {
    const routePath = location.hash;

    if (routePath === '') {
        newsFeed()
    } else if (routePath.indexOf('#/page') != -1) {
        store.currentPage = Number(location.hash.substring(7))
        newsFeed()
    } else {
        newsContent()
    }
}

function getData(url) {
    ajax.open('GET', url, false)
    ajax.send()
    return JSON.parse(ajax.response)
}

window.addEventListener('hashchange', router)
router()
